import React, { FC, useContext, useEffect, useState } from "react";
import LoginForm from "./components/LoginForm";
import { Context } from "./index";
import { observer } from "mobx-react-lite";
import { IUser } from "./models/IUser";
import UserService from "./services/UserService";
import "./App.css";

const App: FC = () => {
  const { store } = useContext(Context);
  const [users, setUsers] = useState<IUser[]>([]);
  useEffect(() => {
    if (localStorage.getItem("token")) {
      store.checkAuth();
    }
  }, []);

  async function getUsers() {
    try {
      const response = await UserService.fetchUsers();
      setUsers(response.data);
    } catch (e) {
      console.log(e);
    }
  }

  // if (store.isLoading) {
  //   return <div>Загрузка...</div>;
  // }

  if (!store.isAuth) {
    return (
      <div className="app-background">
        <div className="header1">
          <div className="menu-logo">
            <a id="a_logo" href="/">
              <img src="https://sun9-13.userapi.com/impg/KqHymWOrbJa5pXlnmTYgqj7i5W4tycBajKOkHg/8arhKJyk4eY.jpg?size=133x54&quality=95&sign=52f7f413b1f4705a4d0ad81feb34b911&type=album"></img>
            </a>
          </div>

          <div className="menu-left" id="id-menu-left">
            <a href="/play" className="forHeader1">
              Играть
            </a>
            <a href="/square" className="forHeader1">
              Команда
            </a>
            <a href="/app" className="forHeader1">
              Приложение
            </a>
          </div>
          <div className="menu-right">
            <form action="/">
              <button id="button-reg" onClick={() => store.logout()}>
                Регистрация
              </button>
            </form>
            <form action="/">
              <button id="button-log" onClick={() => store.logout()}>
                Войти
              </button>
            </form>
          </div>
        </div>
        <div className="main_container">

          <div className="games1">
            <h1 className="top-games">Популярные игры</h1>

          <div className="img-players">
            <img
              className="img_main"
              src="https://opis-cdn.tinkoffjournal.ru/mercury/dota2-in-2023-in.intgihktgvjf..jpg"
            ></img>
            <img
              className="img_main"
              src="https://upload.wikimedia.org/wikipedia/ru/thumb/d/db/Counter-strike_2.jpg/640px-Counter-strike_2.jpg"
            ></img>
            <img
              className="img_main"
              src="https://strikeball-rus.ru/wp-content/uploads/2020/04/battlestate-games-escape-from-tarkov-eft-russia-2028-dikii-s.jpg"
            ></img>
            <img
              className="img_main"
              src="https://cdn.akamai.steamstatic.com/steam/apps/1966720/capsule_616x353.jpg?t=1700231592"
            ></img>
          </div>
          <div className="nickname-players">
            <h2 className="img_main_nickname">Dota 2</h2>
            <h2 className="img_main_nickname">Counter-strike 2</h2>
            <h2 className="img_main_nickname">Escape from Tarkov</h2>
            <h2 className="img_main_nickname">Lethal company</h2>
          </div>
          </div>
          <div className="players1">
            <h1 className="top-teammates">Топ-тиммейты</h1>
            <div className="img-players">
              <img
                className="img_main"
                src="https://global-oss.epal.gg/data/cover/2319680/17043159917954935.jpeg"
              ></img>
              <img
                className="img_main"
                src="https://global-oss.epal.gg/data/cover/1417103/17115550013056719.jpeg"
              ></img>
              <img
                className="img_main"
                src="https://global-oss.epal.gg/data/cover/2302641/17098536637723697.jpeg"
              ></img>
              <img
                className="img_main"
                src="https://global-oss.epal.gg/data/cover/2278183/17078685234682189.jpeg"
              ></img>
            </div>
            <div className="nickname-players">
              <h2 className="img_main_nickname">Whiti</h2>
              <h2 className="img_main_nickname">Paola</h2>
              <h2 className="img_main_nickname">Ahshah ♡</h2>
              <h2 className="img_main_nickname">Yuno</h2>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (store.user.isActivated) {
    return (
      <div>
        <header className="App-header">
          <div className="header1">
            <a href="/" className="forHeader1">
              GNT
            </a>
            <a href="/play" className="forHeader1">
              Play
            </a>
            <a className="forHeader1">Команда</a>
            <a className="forHeader1">Приложение</a>
            <h4>Аккаунт подтвержден по почте</h4>
            <button onClick={getUsers}>Получить пользователей</button>
            <button onClick={() => store.logout()}>Выйти</button>
            {users.map((user) => (
              <div key={user.email}>{user.email}</div>
            ))}
          </div>
        </header>
      </div>
    );
  }

  if (!store.user.isActivated) {
    return (
      <div>
        <header className="App-header">
          <h4>Пожалуйста, подтвердите аккаунт</h4>
        </header>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h3>
          {store.isAuth
            ? `Вы находитесь под логином ${store.user.email}`
            : "Авторизуйтесь, пожалуйста"}
        </h3>
        <button onClick={() => store.logout()}>Выйти</button>
        {users.map((user) => (
          <div key={user.email}>{user.email}</div>
        ))}
      </header>
    </div>
  );
};
export default observer(App); // observer для отслеживания статуса
