import React, { FC, useContext, useState } from "react";
import { Context } from "../index";
import { observer } from "mobx-react-lite";
import { LOGIN_ROUTE, MAIN_ROUTE, REG_ROUTE } from "../utils/consts";
import { NavLink, useLocation, useHistory } from "react-router-dom";
import { Switch, Route, Redirect } from "react-router-dom";

const LoginForm = observer(() => {
  console.log("RegForm");
  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string>("");
  const { store } = useContext(Context);
  console.log("store.user.isActivated", store.user.isActivated)
  return (
    <div className="app-background">
      <img
        className="smesharik"
        src="https://g.epal.gg/entry/us/static/icon_avatar.65e5ba51.png"
      ></img>
      <div className="inputs">
        <div className="input1">
          <input
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            type="text"
            placeholder="Email"
          />
        </div>
        <div className="input2">
          <input
            onChange={(e) => setPassword(e.target.value)}
            value={password}
            type="password"
            placeholder="Пароль"
          />
        </div>
      </div>
      <button
        id="button-log2"
        onClick={() => store.registration(email, password)}
      >
        Зарегистрироваться
      </button>
      <h1>
        {store.isAuth
          ? `Письмо для активации аккаунта отправлено на почту ${store.user.email}. Пожалуйста, перейдите по ссылке в письме, чтобы завершить регистрацию`
          : ""}
      </h1>
    </div>
  );
});

export default LoginForm;
