import React, { FC, useContext, useEffect, useState } from "react";
import LoginForm from "./components/LoginForm";
import { Context } from "./index";
import { observer } from "mobx-react-lite";
import { IUser } from "./models/IUser";
import UserService from "./services/UserService";
import "./App.css";

const App: FC = () => {
  const { store } = useContext(Context);
  const [users, setUsers] = useState<IUser[]>([]);
  useEffect(() => {
    if (localStorage.getItem("token")) {
      store.checkAuth();
    }
  }, []);

  async function getUsers() {
    try {
      const response = await UserService.fetchUsers();
      setUsers(response.data);
    } catch (e) {
      console.log(e);
    }
  }

  if (store.isLoading) {
    return <div>Загрузка...</div>;
  }

  if (!store.isAuth) {
    return (
      <div className="app-background">
        <div className="header1">
          <a href="/" className="menu-logo">.</a>
          <div className="menu-left">
            <a href="/" className="forHeader1">
              GNT
            </a>
            <a href="/play" className="forHeader1">
              Играть
            </a>
            <a href="/square" className="forHeader1">
              Команда
            </a>
            <a href="/app" className="forHeader1">
              Приложение
            </a>
          </div>
        </div>
      </div>
    );
  }

  if (store.user.isActivated) {
    return (
      <div>
        <header className="App-header">
          <div className="header1">
            <a href="/" className="forHeader1">
              GNT
            </a>
            <a href="/play" className="forHeader1">
              Play
            </a>
            <a className="forHeader1">Команда</a>
            <a className="forHeader1">Приложение</a>
            <h4>Аккаунт подтвержден по почте</h4>
            <button onClick={getUsers}>Получить пользователей</button>
            <button onClick={() => store.logout()}>Выйти</button>
            {users.map((user) => (
              <div key={user.email}>{user.email}</div>
            ))}
          </div>
        </header>
      </div>
    );
  }

  if (!store.user.isActivated) {
    return (
      <div>
        <header className="App-header">
          <h4>Пожалуйста, подтвердите аккаунт</h4>
        </header>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h3>
          {store.isAuth
            ? `Вы находитесь под логином ${store.user.email}`
            : "Авторизуйтесь, пожалуйста"}
        </h3>
        <button onClick={() => store.logout()}>Выйти</button>
        {users.map((user) => (
          <div key={user.email}>{user.email}</div>
        ))}
      </header>
    </div>
  );
};
export default observer(App); // observer для отслеживания статуса
